import React, {Component} from 'react'
import FilmList from "./containers/FilmList"

class App extends Component { 
    constructor(props) {
        super(props)
        this.state = {movieList:[]}
    }

    componentWillMount(){
      this.initMovies();
    }

      initMovies() {POPULAR_MOVIES_URL
          axios.get(`${API_END_POINT}&${API_KEY}`).then(function(response){
              this.setState({movieList:response.data.results, currentMovie:response.data.results[0]}, function () {
                  this.applyVideoToCurrentMovie();
              });
          }.bind(this));
      }

    applyVideoToCurrentMovie() {
        axios.get(`${API_END_POINT}movie/${this.state.currentMovie.id}?append_to_response=videos&include_adult=false&${API_KEY}`)
        .then(function(response){
            const youtubeKey = response.data.videos.results[0].key
            let newCurrentMovieState = this.state.currentMovie
            newCurrentMovieState.videoId = youtubeKey
            this.setState({currentMovie : newCurrentMovieState})
        }.bind(this));
    }

    onClickListItem(movie) {
        this.setState({currentMovie:movie}, function () {
            this.applyVideoToCurrentMovie();
            this.setRecommandation();
        })
    }
    render() {
      const renderFilmList = () => {
          if(this.state.movieList.length>=5){
              return <FilmList movieList={this.state.movieList} callback={this.onClickListItem.bind(this)}/>
          }
      }

      return (
          <div>
            <header>
            <div class="navbar navbar-dark bg-dark shadow-sm">
              <div class="container">
              <div class="col-md-2">
                <a href="#">
                  <strong>Popular films</strong>
                </a>
              </div>
              <div class="col-md-2">
                <a href="#">
                  <strong>Autre</strong>
                </a>
              </div>
              </div>
            </div>
          </header>
          <div class="container">
              {renderFilmList()}
          </div>
          </div>

      )
    }
}

export default App;
