import React, {Component} from 'react'
import FilmList from "./containers/FilmList"

class App extends Component { 
    constructor(props) {
        super(props)
        this.state = {movieList:[]}
    }

    render() {
      const renderFilmList = () => {
          if(this.state.movieList.length>=5){
              return <FilmList movieList={this.state.movieList} callback={this.onClickListItem.bind(this)}/>
          }
      }

      return (
          <div>
            <header>
            <div class="navbar navbar-dark bg-dark shadow-sm">
              <div class="container">
              <div class="col-md-2">
                <a href="#">
                  <strong>Popular films</strong>
                </a>
              </div>
              <div class="col-md-2">
                <a href="#">
                  <strong>Autre</strong>
                </a>
              </div>
              </div>
            </div>
          </header>
          <div class="container">
              {renderFilmList()}
          </div>
          </div>

      )
    }
}

export default App;
