import React, {Component} from 'react'
import axios from 'axios'
import FilmList from "./containers/FilmList"

const API_END_POINT = "https://api.themoviedb.org/3/movie/popular?page=1&api_key="
const API_KEY = "f1be4bafe6f7cb0cb84f5948c5b75497"

class App extends Component { 
    constructor(props) {
        super(props)
        this.state = {movieList:[]}
    }

    componentWillMount(){
      this.initMovies();
    }

    initMovies() {
        axios.get(`${API_END_POINT}${API_KEY}`).then(function(response){
            this.setState({movieList:response.data.results, currentMovie:response.data.results[0]}, function () {
              console.log("Films" + this.movieList);
            });
        }.bind(this));
    }

    render() {
      const renderFilmList = () => {
          if(this.state.movieList.length>=5){
              return <FilmList movieList={this.state.movieList}/>
          }
      }

      return (
          <div>
            <header>
            <div className="navbar navbar-dark bg-dark shadow-sm">
              <div className="container">
              <div className="col-md-2">
                <a href="#">
                  <strong>Popular films</strong>
                </a>
              </div>
              <div class="col-md-2">
                <a href="#">
                  <strong>Autre</strong>
                </a>
              </div>
              </div>
            </div>
          </header>
          <div class="container">
              {renderFilmList()}
          </div>
          </div>

      )
    }
}

export default App;
